mapper.select:
select * from meta_entity
where deleted=0
and entity_id = #{entityId,jdbcType=INTEGER}

select count(*) from meta_entity
where deleted=0
and entity_id = #{entityId,jdbcType=INTEGER}

select count(1) from meta_entity t
where t.deleted=0
and t.project_id = #{projectId,jdbcType=INTEGER}

select count(1) from meta_entity t

    and t.deleted=0
    and t.project_id = #{projectId}
    and t.class_name = #{className}

        and t.entity_id != #{entityId}

select count(1) from meta_entity t

    and t.deleted=0
    and t.project_id = #{projectId}
    and t.table_name = #{tableName}

        and t.entity_id != #{entityId}

select m.mtm_id, ( select count(1) from meta_mtm_cascade_ext c where c.mtm_id=m.mtm_id and c.entity_id = t.entity_id and c.cascade_entity_id = t2.entity_id and c.deleted=0 ) as cascade_field_num, t2.* from meta_entity t join meta_mtm m on t.entity_id = m.entity_id1 join meta_entity t2 on t2.entity_id = m.entity_id2 where t.deleted=0 and m.deleted = 0 and t2.deleted = 0 and t.entity_id = #{entityId,jdbcType=INTEGER} and m.hold_refer1 = #{hold,jdbcType=BOOLEAN}
union all
select m.mtm_id, ( select count(1) from meta_mtm_cascade_ext c where c.mtm_id=m.mtm_id and c.entity_id = t.entity_id and c.cascade_entity_id = t2.entity_id and c.deleted=0 ) as cascade_field_num, t2.* from meta_entity t join meta_mtm m on t.entity_id = m.entity_id2 join meta_entity t2 on t2.entity_id = m.entity_id1 where t.deleted=0 and m.deleted = 0 and t2.deleted = 0 and t.entity_id = #{entityId,jdbcType=INTEGER} and m.hold_refer2 = #{hold,jdbcType=BOOLEAN}
