mapper.select:
select * from meta_mtm
where deleted=0
and mtm_id = #{mtmId,jdbcType=INTEGER}

select count(*) from meta_mtm
where deleted=0
and mtm_id = #{mtmId,jdbcType=INTEGER}

select count(1) from meta_mtm t
where t.deleted=0
and (
    t.entity_id1 = #{arg0}
    or t.entity_id2 = #{arg0}
)

select count(1) from meta_mtm t
where t.deleted=0
    and t.entity_id1 = #{entityId1}
    and t.entity_id2 = #{entityId2}

    and t.mtm_id != #{mtmId}
